import { useState, useEffect, useRef } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { ArrowRightCircle } from 'react-bootstrap-icons';
import 'animate.css';
import TrackVisibility from 'react-on-screen';
import { mintNFT, verifyNFT, nftused, tokenURI } from "../utils/interact.js";
import QRCodeStyling from "qr-code-styling";

export const Banner = () => {
  const [loopNum, setLoopNum] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);
  const [text, setText] = useState("");
  const [delta, setDelta] = useState(300 - Math.random() * 100);
  // eslint-disable-next-line no-unused-vars
  const [index, setIndex] = useState(1);
  const toRotate = ["BlockChain", "NFT Developer", "Web3 Developer"];
  const period = 2000;

  //
  const [url, setUrl] = useState("https://qr-code-styling.com");
  const [fileExt, setFileExt] = useState("png");
  const ref = useRef(null);

  useEffect(() => {
    qrCode.append(ref.current);
  }, []);

  useEffect(() => {
    qrCode.update({
      data: url
    });
  }, [url]);

  const onUrlChange = (event) => {
    event.preventDefault();
    setUrl(event.target.value);
  };

  const onExtensionChange = (event) => {
    setFileExt(event.target.value);
  };

  const onDownloadClick = () => {
    qrCode.download({
      extension: fileExt
    });
  };  

  //



  
  useEffect(() => {
    let ticker = setInterval(() => {
      tick();
    }, delta);

    return () => clearInterval(ticker);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [text]);

  const tick = () => {
    let i = loopNum % toRotate.length;
    let fullText = toRotate[i];
    let updatedText = isDeleting
      ? fullText.substring(0, text.length - 1)
      : fullText.substring(0, text.length + 1);

    setText(updatedText);

    if (isDeleting) {
      setDelta((prevDelta) => prevDelta / 2);
    }

    if (!isDeleting && updatedText === fullText) {
      setIsDeleting(true);
      setIndex((prevIndex) => prevIndex - 1);
      setDelta(period);
    } else if (isDeleting && updatedText === "") {
      setIsDeleting(false);
      setLoopNum(loopNum + 1);
      setIndex(1);
      setDelta(500);
    } else {
      setIndex((prevIndex) => prevIndex + 1);
    }
  };

  //mintNFT
  const [count, setcount] = useState("");
  const onMint = async () => {
    setcount(1);
    console.log(count);
    const { status } = await mintNFT(count);
    console.log(status);
    alert(status);
  };

  //verify nft 
  const inputRef = useRef(null);
  const [tokenId, setTokenId] = useState("");
  const onVerify = async () => {
    // ðŸ‘‡ "inputRef.current.value" is input value
    setTokenId(inputRef.current.value);
    if (tokenId < 0 || tokenId > 100) {
      console.log("Please input tokenId for the range 0~99.");
      alert("Please input tokenId for the range 0~99.");
    } else {
      const { status } = await verifyNFT(tokenId);
      console.log(status);
      alert(status);
    }
  };

  //nftused return(uint256)
  const OwnerOfcall = async()=>{
    setTokenId(inputRef.current.value);
    if (tokenId < 0 || tokenId > 100) {
      console.log("Please input tokenId for the range 0~99.");
      alert("Please input tokenId for the range 0~99.");
    } else {
      const { status } = await nftused(tokenId);
      console.log(status);
      alert(status);
    }
  }
  //tokenURI
  const tokenURICall = async()=>{
    setTokenId(inputRef.current.value);
    if (tokenId < 0 || tokenId > 100) {
      console.log("Please input tokenId for the range 0~99.");
      alert("Please input tokenId for the range 0~99.");
    } else {
      const { status } = await tokenURI(tokenId);
      console.log(status);
      alert(status);
    }
  }








  const qrCode = new QRCodeStyling({
    width: 300,
    height: 300,
    image:
      "https://upload.wikimedia.org/wikipedia/commons/5/51/Facebook_f_logo_%282019%29.svg",
    dotsOptions: {
      color: "#4267b2",
      type: "rounded"
    },
    imageOptions: {
      crossOrigin: "anonymous",
      margin: 20
    }
  });
  
  
    
  
    
  const styles = {
    inputWrapper: {
      margin: "20px 0",
      display: "flex",
      justifyContent: "space-between",
      width: "100%"
    },
    inputBox: {
      flexGrow: 1,
      marginRight: 20
    }
  };
  




  return (
    <section className="banner" id="home">
      <Container>
        <Row className="aligh-items-center">
          <Col xs={12} md={6} xl={7}>
            <TrackVisibility>
              {({ isVisible }) => (
                <div
                  className={
                    isVisible ? "animate__animated animate__fadeIn" : ""
                  }
                >
                                   
                  
                  <button onClick={onMint}>
                    Mint NFT! <ArrowRightCircle size={30} />
                  </button>
                  <input
                    ref={inputRef}
                    type="text"
                    placeholder="your tokenId"
                    id="verifytext"
                  />
                  <button onClick={OwnerOfcall}>
                    Check the remaining usage
                    <ArrowRightCircle size={30} />
                  </button>
                  <button onClick={onVerify}>
                    Verify NFT Ticket <ArrowRightCircle size={30} />
                  </button>
                  <button onClick={tokenURICall}>
                    View NFT tokenURI
                    <ArrowRightCircle size={30} />
                  </button>
                </div>
              )}
            </TrackVisibility>
          </Col>
          <Col xs={12} md={6} xl={5}>
            <TrackVisibility>
              {({ isVisible }) => (
                <div className="App">
                <div style={styles.inputWrapper}>
                  <input value={url} onChange={onUrlChange} style={styles.inputBox} />
                  <select onChange={onExtensionChange} value={fileExt}>
                    <option value="png">PNG</option>
                    <option value="jpeg">JPEG</option>
                    <option value="webp">WEBP</option>
                  </select>
                  <button onClick={onDownloadClick}>Download</button>
                </div>
                <div ref={ref} />
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
    </section>
  );
}
